  $(function() {

$('#tab2, #tab3').css('opacity','0').css('display','none');

    $('#tabs div').click(function() {

	var id = $(this).attr('class');
     $("#tab1, #tab2, #tab3").addClass('maxheighthack');
$("#"+id).removeClass('maxheighthack');

      $("#tab1, #tab2, #tab3").animate({
        opacity: 0
      }, 400, function() {
$(this).css('display','none');
$("#"+id).css('display','block');
      });

$("#"+id).css('display','block');

$("#"+id).animate({
        opacity: 1
      }, 400);

  });
  });

$(function() {
