b:if.script:
function setEqualHeight(columns) {
	var tallestcolumn = 0;
	columns.each(
		function() {
			currentHeight = $(this).height();
			if(currentHeight > tallestcolumn) {
				tallestcolumn  = currentHeight;
			}
		}
	);
	columns.height(tallestcolumn);
}
jQuery(document).ready(function($) {
	setEqualHeight($(".post-outer"));
});

