head.script:
//

 // Enter the posts labels here 
cat1 = 'Featured'; 

imgr = new Array();
imgr[0] = "http://3.bp.blogspot.com/-Viba2SFqY9E/VKfHjrChMXI/AAAAAAAAASQ/ayYZUtgFkBE/s580-c/zEk8RJdmQrqja2XwbjgJ_DSC_2368-1024x682.jpg";
showRandomImg = true;
aBold = true;
summaryPost = 150; 
summaryTitle = 50; 
numposts1 = 9

function showrecentposts1(json) {
    j = showRandomImg ? Math.floor((imgr.length + 1) * Math.random()) : 0;
    img = new Array;
    if (numposts1 <= json.feed.entry.length) maxpost = numposts1;
    else maxpost = json.feed.entry.length;
  document.write('<div class="owl_carouselle" style="display:none;">');
    for (var i = 0; i < maxpost; i++) {
        var entry = json.feed.entry[i];
        var posttitle = entry.title.$t;
        var pcm;
        var tag_name = entry.category[0].term;
        var posturl;
        if (i == json.feed.entry.length) break;
        for (var k = 0; k < entry.link.length; k++)
            if (entry.link[k].rel == "alternate") {
                posturl = entry.link[k].href;
                break
            }

        if ("content" in entry) var postcontent = entry.content.$t;
        else if ("summary" in entry) var postcontent = entry.summary.$t;
        else var postcontent = "";

        if (j > imgr.length - 1) j = 0;
        img[i] = imgr[j];
        s = postcontent;
        a = s.indexOf("<img");
        b = s.indexOf('src="', a);
        c = s.indexOf('"', b + 5);
        d = s.substr(b + 5, c - b - 5);
        if (a != -1 && (b != -1 && (c != -1 && d != ""))) img[i] = d;

        var trtd = '<figure class="slider__item"><figcaption class="slide__caption"><div class="post__description"><p><a href="/search/labels/'+tag_name+'?max-results=5" rel="tag">' + tag_name + '</a></p><h2><a href="' + posturl + '">'+posttitle+'</a></h2></div></figcaption><img class="lazyOwl" src="' + img[i].replace(/s\B\d{2,4}/,'s' + 720) + '" alt="" style="display: block;"></figure>';
        document.write(trtd);
        j++
    }
    document.write('</div>')
};

